import { useState } from "react";
import React from 'react';
import { Chessboard } from "react-chessboard";
import './landing-page-chessboard.scss';
import { Chess } from 'chess.js';



export const LandingPageChessBoard: React.FC = () => {
  const [game, setGame] = useState(new Chess());

  function makeAMove(move : any) {
    const gameCopy = new Chess(game.fen());
    const result = gameCopy.move(move);
    setGame(gameCopy);
    return result; // null if the move was illegal, the move object if the move was legal
  }

  function makeRandomMove() {
    const possibleMoves = game.moves();
    if (game.isGameOver() || game.isStalemate() || possibleMoves.length === 0) return; // exit if the game is over
    const randomIndex = Math.floor(Math.random() * possibleMoves.length);
    makeAMove(possibleMoves[randomIndex]);
  }

  function onDrop(sourceSquare: any, targetSquare: any) {
    const promiseEvent = new Promise<Object>((resolve, reject
    ) => {
      setTimeout( function(): void {
        const move = makeAMove({
          from: sourceSquare,
          to: targetSquare,
          promotion: "q", // always promote to a queen for example simplicity
        });
        if (move === null) {
          reject('Invalid move!!!');
        } else {
          resolve(move);
        }
      }, 500);
    });
    promiseEvent.then(( move ) => {
      //setTimeout(makeRandomMove, 1200);
      return true;
    });
    return true;
  }

  return(
    <div className="chessBoard">
      <Chessboard position={game.fen()} onPieceDrop={onDrop}/>
    </div>
  )
}
